function helpBlockDirective(){function e(e,l,n){e.hbVisible=!1,e.iconClass=n.helpBlockIconClass,e.title=n.helpBlockTitle,e.content=n.helpBlockContent,e.toggle=function(){e.hbVisible=!e.hbVisible}}return{scope:{},restrict:"A",replace:!0,template:'<section class="hb-row" ng-class="{\'expanded\': hbVisible}">\n                <p class="hb-title" ng-click="toggle()">\n                  <i ng-if="iconClass" class="{{iconClass}}"></i>\n                  <span ng-bind="title"></span>\n                </p>\n                <p class="hb-content" ng-bind="content" ng-show="hbVisible"></p>\n              </section>',link:e}}angular.module("dm.helpBlock",[]).directive("helpBlock",helpBlockDirective);